(function(){
  // intentar cargar Three.js desde CDN; si falla, marcamos window.THREE = null
  var cdn = "https://cdn.jsdelivr.net/npm/three@0.152.2/build/three.min.js";
  try{
    var s = document.createElement('script');
    s.src = cdn;
    s.onload = function(){ console.log("Three.js cargado desde CDN"); };
    s.onerror = function(){ console.warn("No se pudo cargar Three.js desde CDN. Activando fallback."); window.THREE = null; };
    document.head.appendChild(s);
  }catch(e){
    console.warn("Error cargando Three.js", e);
    window.THREE = null;
  }
})();